package javaprogprac;

public class Whiledemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//while loop
		int i=10;
		while(i>0) {
			System.out.print(i+"\t");
			i--;
		}

		int j=20;
		//do while loop
		do 
		{
			System.out.println(j);
			j++;
		}
		while(j<=30); 

	}

}
